mapper.select:
select
    u.user_id, u.username, u.password, u.nick_name, u.mail, u.phone, u.available, u.avatar_path, u.ctime, u.mtime, u.last_login_time
from neat_user u


        and u.user_id = #{userId}


        and u.username = #{username}


        and u.password = #{password}


        and u.nick_name like concat('%', #{nickName}, '%')


        and u.mail like concat('%', #{mail}, '%')


        and u.phone = #{phone}


        and u.available = #{available}


        and u.dept_id = #{deptId}


order by ctime desc
